        this.addQuestionCreate(db, new Question(89,"Which of the following is a guideline to use when choosing an operating frequency for calling CQ?","Listen first to be sure that no one else is using the frequency","Ask if the frequency is in use","Make sure you are in your assigned band","All of these choices are correct",Question.AnswerEnum.D));
