        this.addQuestionCreate(db, new Question(119,"When using a directional antenna, how might your station be able to access a distant repeater if buildings or obstructions are blocking the direct line of sight path?","Change from vertical to horizontal polarization","Try to find a path that reflects signals to the repeater","Try the long path","Increase the antenna SWR",Question.AnswerEnum.B));
